let minDuration = iif(isempty("$min_duration"), "0", "$min_duration");
let maxDuration = iif(isempty("$max_duration"), "0", "$max_duration");

set truncationmaxrecords=10000;set truncationmaxsize=67108864;set truncationmaxrecords=10000;set truncationmaxsize=67108864;
dependencies
| where $__timeFilter(timestamp)
| where client_Type != "Browser"
| where duration >= todouble(minDuration) and duration <= todouble(maxDuration)
| summarize Count=sum(itemCount), AVG_Duration=sum(duration * itemCount)/sum(itemCount), Duration_95th=percentile(duration,95) by type
| sort by Count desc
| take 3

let minDuration = iif(isempty("$min_duration"), "0", "$min_duration");
let maxDuration = iif(isempty("$max_duration"), "0", "$max_duration");

dependencies
// additional filters can be applied here
| where $__timeFilter(timestamp)
| where client_Type != "Browser"
| extend type=iff(tolower(type) == "http (tracked component)", "HTTP", type), target=tostring(split(target, " | cid-v1")[0])
| extend ["Dependency Name"] = strcat(type, ": ", name, " ", target)
| where ("All" in ($dependency) or ["Dependency Name"] in ($dependency))
| where duration >= todouble(minDuration) and duration <= todouble(maxDuration)
// calculate average request duration for all requests
| summarize Count = count() by bin(timestamp, $__interval)
| order by timestamp asc

let minDuration = iif(isempty("$min_duration"), "0", "$min_duration");
let maxDuration = iif(isempty("$max_duration"), "0", "$max_duration");

// distribution of dependencies by logarithmic duration buckets
let numberOfBuckets = 100;
// filter to a subset of dependencies

let dataset = dependencies
| where client_Type != "Browser"
// additional filters can be applied here
| where $__timeFilter(timestamp)
| extend type=iff(tolower(type) == "http (tracked component)", "HTTP", type), target=tostring(split(target, " | cid-v1")[0])
| extend ["Dependency Name"] = strcat(type, ": ", name, " ", target)
| where ("All" in ($dependency) or ["Dependency Name"] in ($dependency))
| where duration >= todouble(minDuration) and duration <= todouble(maxDuration)
| extend value=duration
| project itemCount, value;
// calculate the maximum duration for all dependencies
let maxValue=toscalar(dataset | summarize max(value));
// calculate the logarithmic base that determines the start of each bucket
// use the max duration to ensure the scale spans all possible durations in the data set
let bucketBase = pow(maxValue, 1/todouble(numberOfBuckets));
// define a table with an index for each bucket to allow for buckets with 0 dependencies
let bucketSlots=range bucketSlot from 0 to numberOfBuckets - 1 step 1;
// map dependency counts to the correct bucket and apply to the scale by joining with the complete set of buckets
bucketSlots | join kind=leftouter (dataset
// any durations under 1ms are rounded up to 1 ms
// calls with durations under 1ms can be viewed in more detail using the linear scale
| extend adjustedValue = iff(value<1, 1.0, value)
// calculate the bucket slot for each dependency by applying logarithmic scale
| extend bucketSlotRaw = tolong(bin(log(adjustedValue)/log(bucketBase), 1))
// calculate the dependency count by duration bucket (rounded to whole number)
| summarize count_=sum(itemCount) by bucketSlot=iff(bucketSlotRaw > numberOfBuckets - 1, numberOfBuckets - 1, bucketSlotRaw)) on bucketSlot
| order by bucketSlot asc
// calculate the real duration value for each bucket based on both the index and the bucket base (rounded to whole number)
| project
    ["Duration (ms)"]=floor(pow(bucketBase, bucketSlot), 1),
    counts=iff(isnull(count_), 0, count_)
| summarize Count=sum(counts) by ["Duration (ms)"]
| where ['Duration (ms)'] >= todouble(minDuration)
| order by ["Duration (ms)"] asc

let minDuration = iif(isempty("$min_duration"), "0", "$min_duration");
let maxDuration = iif(isempty("$max_duration"), "0", "$max_duration");

let min_length_8 = (s: string) {
    let len = strlen(s);
    case(len == 1, strcat(s, s, s, s, s, s, s, s),
        len == 2 or len == 3, strcat(s, s, s, s),
        len == 4 or len == 5 or len == 6 or len == 7, strcat(s, s),
        s)
};
let ai_hash = (s: string) {
    abs(toint(__hash_djb2(min_length_8(s))))
};

dependencies
// additional filters can be applied here
| where $__timeFilter(timestamp)
| extend typeTransform=iff(tolower(type) == "http (tracked component)", "HTTP", type), targetTransform=tostring(split(target, " | cid-v1")[0])
| extend ["Dependency Name"] = strcat(typeTransform, ": ", name, " ", targetTransform)
| where ("All" in ($dependency) or ["Dependency Name"] in ($dependency))
| where duration >= todouble(minDuration) and duration <= todouble(maxDuration)
//| where returncode in ($return_code)
| project ["Dependency Name"], target, ResponseCode = resultCode, ResponseTime = duration, OperationID = operation_Id, Score=ai_hash(operation_Id), type, name
| top 20 by Score asc
